class SubwayNetwork:
    def __init__(self, n: int, edges: List[List[int]]):
        self.n = n
        self.edges = edges
        self.dist = [[float('inf')] * n for _ in range(n)]
        self.prev = [[-1] * n for _ in range(n)]

    def bellman_ford(self, src: int, dst: int) -> List[int]:
        self.dist[src][src] = 0
        for _ in range(self.n - 1):
            for u in range(self.n):
                for v, w in self.edges[u]:
                    if self.dist[u][v] > self.dist[u][u] + w:
                        self.dist[u][v] = self.dist[u][u] + w
                        self.prev[u][v] = u
        path = []
        u = dst
        while u!= -1:
            path.append(u)
            u = self.prev[src][u]
        return path[::-1]

