    input_data = stdin.splitlines()
    n, k = map(int, input_data[0].split())
    p = list(map(int, input_data[1].split()))

    # Check if the given permutation is stack-sortable
    if len(set(p[:k])) == k:
        a = p[:k]
        s = []
        b = []

        while a or s:
            if a:
                a_top = a.pop(0)
                s.append(a_top)

            if s:
                s_top = s.pop()
                b.append(s_top)

        if sorted(b) == p[:k]:
            # If the first k elements are stack-sortable, find the remaining elements
            remaining_elements = [x for x in range(1, n + 1) if x not in p[:k]]
            stack_sortable_p = a + s + b + remaining_elements
            stack_sortable_p.sort(reverse=True)
            return ' '.join(map(str, stack_sortable_p))

    return -1

