    digits = [int(d) for d in num]

    def backtrack(start: int, digits: list, path: str) -> None:
        nonlocal max_num
        if len(path) == n and int(path) <= b:
            if max_num == "" or int(max_num) < int(path):
                max_num = path
        else:
