    n, k = map(int, stdin.split())
    if k > n * (n + 1) // 2:
        return -1

    matrix = [[0] * n for _ in range(n)]
    row, col = 0, 0
    for i in range(k):
        matrix[row][col] = 1
        if col + 1 < n:
            col += 1
        elif row + 1 < n:
            row += 1
            col = 0
        else:
            break

    # Check if the matrix is symmetrical
    for i in range(n):
        for j in range(n):
            if matrix[i][j] != matrix[j][i]:
                return -1

    # Print the matrix
    result = ""
    for row in matrix:
        result += " ".join(str(x) for x in row) + "\n"
    return result.rstrip()

