    # Function to find the Manhattan distance between two points
    def manhattan_distance(a, b):
        return abs(a[0] - b[0]) + abs(a[1] - b[1])

    # Function to check if a move is valid
    def is_valid_move(piece, square, board):
        if piece == 'K':
            moves = [(2, 1), (2, -1), (1, 2), (1, -2), (1, 1), (1, -1), (2, 2), (2, -2)]
        elif piece == 'B':
            moves = [(-1, -1), (-1, 1), (1, -1), (1, 1)]
        elif piece == 'R':
            moves = [(-1, 0), (1, 0), (0, -1), (0, 1)]
        return any(manhattan_distance(square, (i, j)) == 1 and board[i][j] != 0 for (i, j) in moves)

    # Function to find the minimum number of steps and replacements to reach the end
    def min_steps_and_replacements(board, start_square, start_piece):
        visited = set()
        replacements = 0
        steps = 0
        current_square = start_square
        current_piece = start_piece

        def dfs(current_square, current_piece, visited, replacements, steps):
            nonlocal end_square
            if current_square == end_square:
                return steps

            if current_square in visited:
                return float('inf')

            visited.add(current_square)

            for i, j in [(1, 2), (2, 1), (2, -1), (1, -2), (1, 2), (1, -1), (2, 2), (2, -2)]:
                new_square = (current_square[0] + i, current_square[1] + j)
                new_piece = None

                if is_valid_move(current_piece, new_square, board):
                    if i == 2 and j == 2:
                        new_piece = current_piece
                    else:
                        new_piece = 'R' if current_piece == 'K' else 'K'

                    if new_piece != current_piece and current_piece != 'R':
                        replacements += 1

                    new_steps = dfs(new_square, new_piece, visited, replacements, steps + 1)

                    if new_steps != float('inf'):
                        return new_steps

            return float('inf')

        end_square = (N - 1, N - 1)
        min_steps = dfs(start_square, start_piece, visited, replacements, steps)
        return min_steps, replacements

    # Find the starting square and piece
    start_square = None
    start_piece = None
    for i in range(N):
        for j in range(N):
            if A[i][j] == 1:
                start_square = (i, j)
                start_piece = 'K'

    if start_square is None:
        start_square = (0, 0)
        start_piece = 'K'

    # Find the end square and piece
    end_square = (N - 1, N - 1)
    end_piece = 'R'

    # Find the minimum number of steps and replacements to reach the end
    min_steps, min_replacements = min_steps_and_replacements(A, start_square, start_piece)

    return str(min_steps) + ' ' + str(min_replacements)

