    mod = 10**9 + 7

    def count_ways(k: int, n: int, m: int, used: list):
        if k == 0:
            return 1
        if m == 0:
            return 0
        total = 0
        for i in range(n):
            if i not in used:
                new_used = used.copy()
                new_used.append(i)
                total += count_ways(k - 1, n, m - 1, new_used)
                total %= mod
        return total

    result = count_ways(n, n, m, [])

    return str(result)

