        total_cost = 0
        num_bottles = 0
        for i in range(n):
            volume = 2**i - 1
            if volume <= L:
                bottles = L // volume
                L %= volume
                total_cost += bottles * c[i]
                num_bottles += bottles
        return total_cost

    if L == 0:
        return 0
    else:
        return find_bottles(L, c)


