    if yb + r > yw:
        return "-1"

    def reflection_coefficient(y: float, yw: float) -> float:
        return 2 * yw - 2 * y

    def bouncing_probability(y: float, yw: float, yb: float) -> float:
        reflection = reflection_coefficient(y, yw)
        return (reflection - yb) / (reflection + yb)

    def abscissa_of_bounce(y: float, yw: float) -> float:
        reflection = reflection_coefficient(y, yw)
        return (xb + r) * (reflection - y) / (reflection - y + yb)

    def abscissa_of_goal(y: float, yw: float, yb: float) -> float:
        probability = bouncing_probability(y, yw, yb)
        return abscissa_of_bounce(y, yw) - (1 - probability) * (2 * r)

    def find_xw() -> float:
        y = yw_1
        while y < y2:
            xw = abscissa_of_goal(y, yw_1, yb_1)
            y += 1
            if yb < xw < yw:
                return xw
        return -1

    xw = find_xw()
    if xw != -1:
        return f"{xw:.10f}"
    else:
        return "-1"

