    def is_correct(expression: str) -> bool:
        stack = []
        for char in expression:
            if char == '0' or char == '1':
                stack.append(char)
            elif char == '->':
                if len(stack) < 2:
                    return False
                if stack[-2] == '-':
                    stack.pop()
                    stack.pop()
                elif stack[-1] == '(':
                    stack.pop()
                else:
                    stack.append('->')
            elif char == '(':
                stack.append(char)
            elif char == ')':
                if len(stack) < 1:
                    return False
                if stack[-1] == '-':
                    stack.pop()
                    stack.pop()
                elif stack[-1] == '(':
                    stack.pop()
                else:
                    return False
        return len(stack) == 1 and stack[0] == '1'

    def implication(a1: str, a2: str) -> str:
        return f"({a1}->{a2})"

    def generate_expression(args: list[int], index: int) -> str:
        if index == len(args):
            return '1'
        else:
            return generate_expression(args, index + 1) + f"({args[index]}-({implication(args[index], generate_expression(args, index + 1))}-{args[index]})))"

    expression = generate_expression(args, 0)

    if not is_correct(expression):
        return "NO"
    else:
        return "YES\n" + expression

