    # if n is already rounded to 0, return 0
    if n % 10 == 0:
        return str(n)

    # calculate the nearest multiple of 10 that is greater than n
    next_multiple = (n // 10 + 1) * 10

    # calculate the nearest multiple of 10 that is less than n
    prev_multiple = (n // 10) * 10

    # calculate the difference between n and the next multiple of 10
    diff_next = abs(next_multiple - n)

    # calculate the difference between n and the previous multiple of 10
    diff_prev = abs(prev_multiple - n)

    # determine the correct answer based on the difference between n and the nearest multiple of 10
    if diff_next < diff_prev:
        return str(next_multiple)
    else:
        return str(prev_multiple)

