    strings = [(string[0], int(string[1])) for string in strings]

    # Initialize a dictionary to store the palindrome substrings and their counts
    palindromes = {}

    # Iterate through the strings
    for string, cost in strings:
        # Iterate through the length of the string
        for i in range(len(string)):
            for j in range(i, len(string)):
                # Check if the substring is a palindrome
                substring = string[i:j+1]
                if substring == substring[::-1]:
                    # If the palindrome is already in the dictionary, update its count
                    if substring in palindromes:
                        palindromes[substring] = max(palindromes[substring], cost)
                    else:
                        # If the palindrome is not in the dictionary, add it with its cost
                        palindromes[substring] = cost

    # Initialize the minimum cost variable
    min_cost = float('inf')

    # Iterate through the palindromes dictionary
    for substring, cost in palindromes.items():
        # Check if the substring is a palindrome
        if substring == substring[::-1]:
            # Calculate the length of the palindrome
            length = len(substring)

            # Calculate the number of times to use the palindrome
            num_times = int((N + length - 1) / length)

            # Calculate the total cost for the palindrome
            total_cost = cost * num_times

            # Update the minimum cost if the total cost is less than the current minimum
            min_cost = min(min_cost, total_cost)

    # If there is no palindrome, return -1
    if min_cost == float('inf'):
        return -1
    else:
        return min_cost

