        return abs((x1*(y2-y3) + x2*(y3-y1) + x3*(y1-y2))/2)

    def is_covered(x, y, x1, y1, x2, y2, x3, y3):
        if x <= max(x1, x2, x3):
            if y <= max(y1, y2, y3):
                if x1 == x2 == x3 or y1 == y2 == y3:
                    return False
                if x == x1 or y == y1:
                    return area(x2, y2, x3, y3, x1, y1) <= 0
                elif x == x2 or y == y2:
                    return area(x1, y1, x3, y3, x2, y2) <= 0
                elif x == x3 or y == y3:
                    return area(x1, y1, x2, y2, x3, y3) <= 0
            else:
                return False
        else:
            return False

    def main(n, points):
        points.sort(key=lambda x: x[0])
        min_dist = float('inf')
        for i in range(n-1):
            x1, y1 = points[i]
            x2, y2 = points[i+1]
            for j in range(i+1, n):
                x3, y3 = points[j]
                if is_covered(x1, y1, x2, y2, x3, y3):
                    dist = (x1-x2)**2 + (y1-y2)**2
                    if dist < min_dist:
                        min_dist = dist
        return int(min_dist**0.5)

    n = int(stdin.readline())
    points = [tuple(map(int, stdin.readline().split())) for _ in range(n)]
    return str(main(n, points))


