    input_lines = stdin.split('\n')
    n = int(input_lines[0])
    intervals = []
    for i in range(1, n+1):
        l, r = map(int, input_lines[i].split())
        intervals.append((l, r))

    def is_intersect(interval1, interval2):
        return interval1[1] >= interval2[0] and interval1[0] <= interval2[1]

    def intersect_length(interval1, interval2):
        return max(0, min(interval1[1], interval2[1]) - max(interval1[0], interval2[0]))

    def merge_intervals(intervals):
        intervals.sort(key=lambda x: x[0])
        merged_intervals = [intervals[0]]
        for interval in intervals[1:]:
            last_interval = merged_intervals[-1]
            if is_intersect(last_interval, interval):
                merged_intervals[-1] = (last_interval[0], max(last_interval[1], interval[1]))
            else:
                merged_intervals.append(interval)
        return merged_intervals

    def check_accept(intervals, start, end):
        accepted = []
        for interval in intervals:
            if start <= interval[0] and interval[1] <= end:
                accepted.append(interval)
            elif is_intersect(interval, (start, end)):
                return False, []
        return True, accepted

    def dfs(intervals, accepted):
        if not intervals:
            return len(accepted)
        start, end = intervals[0]
        can_accept, new_accepted = check_accept(accepted, start, end)
        if can_accept:
            new_accepted.append(intervals[0])
            return dfs(intervals[1:], new_accepted)
        else:
            return dfs(intervals[1:], accepted)

    intervals = merge_intervals(intervals)
    return str(dfs(intervals, []))

