    def prime(x):
        prime_divisors = set()
        for i in range(2, int(x**0.5) + 1):
            if x % i == 0:
                prime_divisors.add(i)
                while x % i == 0:
                    x //= i
        if x > 1:
            prime_divisors.add(x)
        return prime_divisors

    def g(x, p):
        k = 0
        while x % (p**k) == 0:
            k += 1
        return p**(k-1)

    def f(x, y):
        return prod([g(y, p) for p in prime(x)])

    def prod(iterable):
        result = 1
        for num in iterable:
            result *= num
        return result

    answer = prod([f(x, i) for i in range(1, n+1)]) % mod
    return str(answer)

