def solution(stdin: str) -> str:
    n, m, k = map(int, stdin.split())
    maze = [input().split() for _ in range(n)]
    visited = [[False for _ in range(m)] for _ in range(n)]

    def is_valid(x, y):
        return 0 <= x < n and 0 <= y < m and maze[x][y] == "." and not visited[x][y]

    def find_start():
        for i in range(n):
            for j in range(m):
                if maze[i][j] == "X":
                    return i, j
        return None, None

    def bfs(start_x, start_y):
        queue = [(start_x, start_y, "")]
        visited[start_x][start_y] = True

        while queue:
            x, y, way = queue.pop(0)

            for dx, dy in [(-1, 0), (1, 0), (0, -1), (0, 1)]:
                nx, ny = x + dx, y + dy
                if is_valid(nx, ny):
                    queue.append((nx, ny, way + maze[nx][ny]))
                    visited[nx][ny] = True
                    if len(way + maze[nx][ny]) == k:
                        return way + maze[nx][ny]
        return None

    start_x, start_y = find_start()
    if start_x is None:
        return "IMPOSSIBLE"
    cycle = bfs(start_x, start_y)
    return cycle if cycle is not None else "IMPOSSIBLE"

