        for i in range(len(binary)):
            decimal += int(binary[i]) * (2 ** (len(binary) - 1 - i))
        return decimal

    # Function to calculate the max value of a string
    def max_value(string: str, max_length: int) -> int:
        max_value = 0
        for i in range(max_length):
            max_value += binary_to_decimal(string[i * 2: i * 2 + 2])
        return max_value

    # Function to calculate the min value of a string
    def min_value(string: str, max_length: int) -> int:
        min_value = 0
        for i in range(max_length):
            min_value += (2 ** 2 - binary_to_decimal(string[i * 2: i * 2 + 2]))
        return min_value

