    # Calculate the number of ways to paint the blocks with at most K pairs of adjacent blocks painted in the same color
    def paint_blocks(N: int, M: int, K: int) -> int:
        if K == 0:
            return M ** N

        if N == 1:
            return M

        if K == 1:
            return (M * (M - 1)) * (M ** (N - 1))

        total = 0
        for i in range(K + 1):
            total += (M - 1) ** i * M ** (K - i) * paint_blocks(N - K, M, i)

        return total % 998244353

    # Calculate the number of ways to paint the blocks without the constraint of at most K pairs of adjacent blocks painted in the same color
    def paint_blocks_without_constraint(N: int, M: int, K: int) -> int:
        if N == 1:
            return M

        total = 0
        for i in range(1, K + 1):
            total += (M - 1) ** i * M ** (K - i) * paint_blocks_without_constraint(N - K, M, i)

        return total % 998244353

    # Calculate the final answer
    answer = (paint_blocks(N, M, K) - paint_blocks_without_constraint(N, M, K)) % 998244353

    return str(answer)

