        return True

    def modular_pow(base: int, exponent: int, modulus: int) -> int:
        result = 1
        base = base % modulus
        while exponent > 0:
            if exponent % 2 == 1:
                result = (result * base) % modulus
            base = (base * base) % modulus
            exponent = exponent // 2
        return result

    def find_polynomial(p: int, a: List[int]) -> List[int]:
        mod = 2 ** p - 1
        f = [0] * (p - 1)
        for i in range(p - 1):
            f[i] = modular_pow(a[i], p - i - 1, mod)
        return f

