            for j in range(i + 1, n):
                if not is_coprime(correct_solutions[i], correct_solutions[j]) or not is_coprime(wrong_solutions[i], wrong_solutions[j]):
                    return False

        return True

    def find_min_tl(n: int, m: int, correct_solutions: List[int], wrong_solutions: List[int]) -> int:
        lo, hi = 0, 100
        while lo <= hi:
            mid = (lo + hi) // 2
            if is_valid_tl(n, m, correct_solutions, wrong_solutions, mid):
