    def calculate(expression: str) -> int:
        stack = []
        for char in expression:
            if char.isdigit():
                stack.append(int(char))
            elif char == '+':
                stack.append(stack.pop() + stack.pop())
            elif char == '*':
                stack.append(stack.pop() * stack.pop())
        return stack[-1]

    def add_brackets(expression: str, index: int) -> str:
        if index == 0:
            return "(" + expression + ")"
        return add_brackets(expression[:index], 0) + "(" + expression[index:] + ")"

    def maximize_value(expression: str) -> int:
        max_value = 0
        for i in range(len(expression) - 1):
            if expression[i] == '+' and expression[i + 1] == '*':
                new_expression = add_brackets(expression[:i + 1], i)
                new_value = calculate(new_expression)
                max_value = max(max_value, new_value)
        return max_value

    expression = stdin.replace("+", " + ").replace("*", " * ")
    return maximize_value(expression)

