        for i in range(n - 1):
            if path[i] + 1 != path[i + 1]:
                return False

        return True

    def get_path(start, end):
        path = [start]
        current = start
        for _ in range(n - 1):
            current += 1
            if current > end:
                current -= n
            path.append(current)
        return path

