    if len(possible_paths) > 1:
        return -1

    if possible_paths:
        path = possible_paths[0]
        roads = set()
        for i in range(n - 1):
            roads.add((path[i], path[i + 1]))

        if len(roads) > k:
            return -1

        result = [v for v in path]
        result.insert(0, b)
        result.append(d)

