def solution(stdin: str) -> str:
    input_data = stdin.split("\n")
    N, M = map(int, input_data[0].split())
    S = list(map(int, input_data[1].split()))
    T = list(map(int, input_data[2].split()))

    MOD = 10**9 + 7
    dp_S = [1] * N
    dp_T = [1] * M

    for i in range(N):
        for j in range(i):
            if S[i] == S[j]:
                dp_S[i] += dp_S[j]
                dp_S[i] %= MOD

    for i in range(M):
        for j in range(i):
            if T[i] == T[j]:
                dp_T[i] += dp_T[j]
                dp_T[i] %= MOD

    ans = sum(dp_S[i] * dp_T[j] for i in range(N) for j in range(M)) % MOD

    return str(ans)

def solution(stdin: str) -> str:
    n = int(stdin.splitlines()[0])
    a = list(map(int, stdin.splitlines()[1].split()))

    # Calculate the sum of the first n elements and the last n elements
    first_sum = sum(a[:n])
    last_sum = sum(a[n:])

    # Check if there's no solution
    if first_sum == last_sum:
        return "-1"

    # Calculate the difference between the sums
    diff = abs(first_sum - last_sum)

    # Create an empty list to store the reordered elements
    reordered_a = []

    # Iterate through the array and add elements to the reordered list
    for i in range(n):
        if a[i] == 1:
            reordered_a.append(1)
        elif a[i] == 2:
            reordered_a.append(2)
            if diff > 0:
                reordered_a.append(1)
                diff -= 1
            elif diff < 0:
                reordered_a.append(2)
                diff += 1

    # Iterate through the remaining elements and add them to the reordered list
    for i in range(n, 2 * n):
        reordered_a.append(a[i])

    # Join the reordered list into a string and return it
    return " ".join(map(str, reordered_a))

