def solution(stdin: str) -> str:
    n, k = map(int, input().split())

    if n == 1 and k == 1:
        return "1"

    # Calculate the number of ways to fill in each row
    row_ways = [1] * n
    for i in range(1, n):
        row_ways[i] = (k - i + 1) * row_ways[i-1] % (10**9 + 7)

    # Calculate the number of ways to fill in each column
    col_ways = [1] * n
    for j in range(1, n):
        col_ways[j] = (k - j + 1) * col_ways[j-1] % (10**9 + 7)

    # Calculate the total number of ways
    total_ways = 1
    for i in range(n):
        total_ways = (total_ways * row_ways[i] * col_ways[i]) % (10**9 + 7)

    return str(total_ways)

