def solution(stdin: str) -> str:
    n = int(stdin.splitlines()[0])
    a = list(map(int, stdin.splitlines()[1].split()))

    def mex(b):
        m = 1
        while m in b:
            m += 1
        return m

    def subarrays(a):
        for i in range(1, n):
            yield a[i:]
            yield a[:n - i]

    mems = [mex(subarray) for subarray in subarrays(a)]
    mex_mex = mex(mems)

    return str(mex_mex)


