def solution(stdin: str) -> str:
    n = int(stdin.split("\n")[0])
    s = stdin.split("\n")[1]

    def is_good(sub: str) -> bool:
        return sub.count('0') != sub.count('1')

    if is_good(s):
        return "1\n1"

    cuts = []
    i = 0
    while i < n:
        j = i + 1
        while j <= n and is_good(s[i:j]):
            j += 1
        cuts.append(j - i)
        i = j

    k = min(cuts)
    i = 0
    result = []
    while i < n:
        j = i + k
        while j < n + 1 and is_good(s[i:j]):
            j += 1
        result.append(s[i:j - 1])
        i = j

    return f"{k}\n{' '.join(result)}"

