    from itertools import combinations

    def tree_to_graph(n: int, edges: list) -> tuple:
        adj_list = defaultdict(set)
        for u, v in edges:
            adj_list[u].add(v)
            adj_list[v].add(u)
        return adj_list

    def num_independent_sets(G: dict) -> int:
        n = len(G)
        count = 0
        for subset in combinations(G.keys(), n):
            if len(set.union(*G.values())) == len(set(range(1, n+1))):
                count += 1
        return count

    def edge_induced_subgraph_sum(G: dict, E: set) -> int:
        return num_independent_sets(G[E])

    def compute_sum(n: int, edges: list) -> int:
        G = tree_to_graph(n, edges)
        sum_result = 0
        for E in combinations(G.keys(), n):
            sum_result += edge_induced_subgraph_sum(G, E)
        return sum_result % 998244353

    n = int(stdin.split('\n')[0])
    edges = [tuple(map(int, line.split())) for line in stdin.split('\n')[1:]]

    return str(compute_sum(n, edges))

