        return sum(set(codes[start:end + 1]))

    def partition_comfort(partition):
        return sum(xor_comfort(start, end) for start, end in partition)

    def max_partition_comfort(partition_sizes):
        min_size = min(partition_sizes)
        max_partition_size = max(partition_sizes)
        min_partition_sizes = [i for i in partition_sizes if i == min_size]
        max_partition_sizes = [i for i in partition_sizes if i == max_partition_size]

        min_partition_comfort = sum(partition_comfort([(start, start) for start in range(len(codes)) if codes[start] == i]) for i in min_partition_sizes)
        max_partition_comfort = sum(partition_comfort([(start, start) for start in range(len(codes)) if codes[start] == i]) for i in max_partition_sizes)

        return max(min_partition_comfort, max_partition_comfort)

    partition_sizes = [0] * n
    for i in range(n):
        partition_sizes[codes[i] - 1] += 1

    return str(max_partition_comfort(partition_sizes))

