def solution(stdin: str) -> str:
    input_data = stdin.split('\n')
    N = int(input_data[0])
    edges = [(int(a), int(b)) for a, b in zip(input_data[1:N], input_data[2:2 * N:2])]
    tree = {i: [] for i in range(1, N + 1)}
    for i, (a, b) in enumerate(edges):
        tree[a].append((b, i))
        tree[b].append((a, i))

    def find_path(start, end, k, visited=None, path=None):
        if visited is None:
            visited = set()
            path = []
        visited.add(start)
        if start == k:
            path.append(k)
        if start == end:
            return path + [end]
        for neighbor, edge_id in tree[start]:
            if neighbor not in visited:
                path_to_end = find_path(neighbor, end, k, visited.copy(), path.copy() + [edge_id])
                if path_to_end:
                    return path_to_end
        return []

    Q, K = map(int, input_data[N * 2 - 2:N * 2 - 1])
    queries = [(int(a), int(b)) for a, b in zip(input_data[N * 2 - 1:], input_data[N * 2:N * 2 + Q])]
    responses = []
    for x, y in queries:
        path = find_path(x, y, K)
        if path:
            responses.append(sum(c[1] for c in edges[path[1:-1]]) + 1)
        else:
            responses.append(-1)
    return '\n'.join(map(str, responses))

