        rows[i][0:10] = row

    def roll_dice():
        return random.randint(1, 6)

    def climb_ladder(rows, start_row, start_col, direction):
        new_row, new_col = start_row, start_col
        while True:
            new_row += direction
            if new_row < 0 or new_row >= n:
                return new_row, new_col
            if rows[new_row][new_col] == 0:
                return new_row, new_col
            new_col += direction
            if new_col < 0 or new_col >= n:
                return new_row, new_col
            if rows[new_row][new_col] == 0:
                return new_row, new_col
        return new_row, new_col

    def play_game(rows, start_row, start_col, start_direction, turn):
        if start_row == 9 and start_col == 9:
            return 0

        if rows[start_row][start_col] == 0:
            start_row, start_col = climb_ladder(rows, start_row, start_col, start_direction)
        else:
            start_row += start_direction

        if start_row < 0 or start_row >= n:
            return float('inf')
        if start_col < 0 or start_col >= n:
            return float('inf')

        next_direction = 1 if start_direction == 0 else 0
        roll_result = roll_dice()
        if rows[start_row][start_col] == 0:
            return float('inf')
        next_row, next_col = start_row, start_col
        for _ in range(roll_result):
            next_row += next_direction
            if next_row < 0 or next_row >= n:
                return float('inf')
            if rows[next_row][next_col] == 0:
                return float('inf')
            next_col += next_direction
            if next_col < 0 or next_col >= n:
                return float('inf')
            if rows[next_row][next_col] == 0:
                return float('inf')

        if next_row == 9 and next_col == 9:
            return turn

        return play_game(rows, next_row, next_col, next_direction, turn + 1)

    return play_game(rows, 0, 0, 0, 1)

