    n, m = map(int, input_list[0].split())
    graph = [list(map(int, input_list[i].split())) for i in range(1, m + 1)]

    # Bus topology
    if len(graph) == 1 and n > 2:
        return "bus topology"

    # Ring topology
    for i in range(n):
        if (i + 1) % n == 0:
            if not graph[i]:
                continue
            if graph[i][0] == i + 1 or graph[i][1] == i + 1:
                return "ring topology"

    # Star topology
    star = False
    for i in range(n):
        if len(graph[i]) == n - 1:
            star = True
            break
    if star:
        return "star topology"

    return "unknown topology"

