        return True

    def replace_char(s: str, i: int, m: int, char: str) -> str:
        return s[:i] + char + s[i + m:]

    def count_beauties(s: str, m: int) -> int:
        n = len(s)
        count = 0
        for i in range(0, n - m + 1):
            if is_valid_occurrence(s, i, m):
                count += 1
        return count

