    s = s.strip()
    t = "abab" * (m // 4)
    if m % 4 == 1:
        t += "a"
    elif m % 4 == 2:
        t += "b"
    elif m % 4 == 3:
        t += "ab"

    beauty = count_beauties(s, m)
    min_replacements = float('inf')

    for i in range(n - m + 1):
        if s[i:i + m] == t:
            continue

        for char in ('a', 'b'):
            modified_s = replace_char(s, i, m, char)
            modified_beauty = count_beauties(modified_s, m)

            if modified_beauty > beauty:
                min_replacements = min(min_replacements, 1 + count_beauties(replace_char(modified_s, i, m, '?'), m))

