def solution(stdin: str) -> str:
    input_list = stdin.split('\n')
    n = int(input_list[0])

    def is_prime(num):
        if num <= 1:
            return False
        for i in range(2, int(num**0.5) + 1):
            if num % i == 0:
                return False
        return True

    def find_prime_degrees(edges, n):
        prime_degrees = []
        vertex_degrees = [0] * n
        for edge in edges:
            u, v = edge[0], edge[1]
            vertex_degrees[u - 1] += 1
            vertex_degrees[v - 1] += 1
        for degree in vertex_degrees:
            if is_prime(degree):
                prime_degrees.append(degree)
        return prime_degrees

    def find_edges(prime_degrees, n):
        edges = []
        while prime_degrees:
            degree = prime_degrees.pop(0)
            for i in range(n):
                for j in range(i + 1, n):
                    edges.append((i + 1, j + 1))
                    if len(edges) == degree:
                        return edges
        return edges

    def find_valid_graph(n):
        if not is_prime(n * (n - 1) // 2):
            return None
        for i in range(1, n + 1):
            for j in range(i + 1, n + 1):
                if not is_prime(i + j):
                    return None
        return True

    if not find_valid_graph(n):
        return str(-1)

    edges = find_edges(find_prime_degrees(find_edges(find_prime_degrees([], n), n), n), n)
    m = len(edges)
    return str(m) + '\n' + '\n'.join([str(edge[0]) + ' ' + str(edge[1]) for edge in edges])

