    def count_consecutive_ones(arr):
        count = 0
        max_count = 0
        for i in arr:
            if i == 1:
                count += 1
            else:
                max_count = max(max_count, count)
                count = 0
        return max(max_count, count)

    # Count consecutive ones in the original array
    max_count = count_consecutive_ones(a)

    # If k is 0 or max_count is n, we can't change any zeroes to ones
    if k == 0 or max_count == n:
        return f"{max_count}\n{' '.join(map(str, a))}"

    # Initialize a list to store the new array
    new_a = [0] * n

    # Iterate through the original array
    for i in range(n):
        # If the current element is 1, copy it to the new array
        if a[i] == 1:
            new_a[i] = 1
        # If the current element is 0 and we haven't exceeded k changes, change it to 1
        elif a[i] == 0 and k > 0:
            new_a[i] = 1
            k -= 1
        # If the current element is 0 and we have exceeded k changes, copy the original element
        else:
            new_a[i] = a[i]

