    input_data = stdin.split('\n')
    table = [list(row) for row in input_data[:9]]
    last_move_x, last_move_y = map(int, input_data[9].split())

    def is_empty(x, y):
        return table[x][y] == '.'

    def is_occupied(x, y):
        return table[x][y] in ('x', 'o')

    def is_free(x, y):
        return is_empty(x, y) and any(is_empty(x + dx, y + dy) for dx in (-1, 0, 1) for dy in (-1, 0, 1) if not (dx == 0 and dy == 0) and (0 <= x + dx <= 8) and (0 <= y + dy <= 8))

    def can_move(x, y):
        if is_occupied(x, y):
            return False
        if is_empty(x, y):
            return True
        dx, dy = 0, 0
        while True:
            dx, dy = (dx + x + 1) % 9, (dy + y + 1) % 9
            if is_empty(dx, dy):
                return True
            if is_occupied(dx, dy):
                return False

    def next_move(x, y):
        if is_occupied(x, y):
            return None
        if is_empty(x, y):
            return x, y
        dx, dy = 0, 0
        while True:
            dx, dy = (dx + x + 1) % 9, (dy + y + 1) % 9
            if is_empty(dx, dy):
                return dx, dy
            if is_occupied(dx, dy):
                break
        return None

    def print_table(table):
        for row in table:
            print(''.join(row))

    empty_cells = [(x, y) for x in range(9) for y in range(9) if is_empty(x, y)]
    last_move_cells = [(x, y) for x in range(9) for y in range(9) if (x, y) == (last_move_x - 1, last_move_y - 1)]

    moves = []
    for x, y in empty_cells:
        if can_move(x, y):
            next_x, next_y = next_move(x, y)
            if next_x is not None and next_y is not None:
                moves.append((x, y, next_x, next_y))

    output_table = [['!' if (x, y) in moves else table[x][y] for y in range(9)] for x in range(9)]

    print_table(output_table)

