        return n, c1, c2, c3, edges

    def paint_vertices(n, c1, c2, c3, edges):
        def dfs(v, p, col, colors):
            colors[v] = col
            for u, _ in edges:
                if u != p:
                    dfs(u, v, (col + 1) % 3, colors)

        colors = [-1] * n
        dfs(1, None, 0, colors)
        return colors

    def is_good_painting(colors):
        for i in range(len(colors)):
            for j in range(i + 1, len(colors)):
                if colors[i] == colors[j]:
                    return False
        for i in range(len(colors) - 2):
            for j in range(i + 1, len(colors) - 1):
                for k in range(j + 1, len(colors)):
                    if colors[i] == colors[j] and colors[j] == colors[k]:
                        return False
        return True

    def find_min_cost_painting(n, c1, c2, c3, edges):
        colors = paint_vertices(n, c1, c2, c3, edges)
        if not is_good_painting(colors):
            return -1, None
        min_cost = sum(c1 if color == 0 else c2 if color == 1 else c3 for color in colors)
        return min_cost, colors

    def main():
        n, c1, c2, c3, edges = read_input()
        min_cost, colors = find_min_cost_painting(n, c1, c2, c3, edges)
        if min_cost == -1:
            return "-1\n"
        return f"{min_cost}\n{' '.join(map(str, colors))}\n"

    return main()

