    result = [0] * (len(s) + 1)

    def is_valid(x, y):
        return 0 <= x < x and 0 <= y < y

    def move(x, y, direction):
        if direction == 'L':
            return x, y - 1
        elif direction == 'R':
            return x, y + 1
        elif direction == 'U':
            return x - 1, y
        elif direction == 'D':
            return x + 1, y

    def search(k, x, y):
        if k == len(s) or visited[x][y]:
            return 1
        visited[x][y] = True
        count = 0
        for direction in s[:k + 1]:
            new_x, new_y = move(x, y, direction)
            if is_valid(new_x, new_y) and not visited[new_x][new_y]:
                count += search(k + 1, new_x, new_y)
        visited[x][y] = False
        return count

    result[0] = 1
    for i in range(1, len(result)):
        result[i] = search(i - 1, x_0 - 1, y_0 - 1)

    return ' '.join(map(str, result))

