    def base_64_to_int(c: str) -> int:
        if '0' <= c <= '9':
            return ord(c) - ord('0')
        elif 'A' <= c <= 'Z':
            return ord(c) - ord('A') + 10
        elif 'a' <= c <= 'z':
            return ord(c) - ord('a') + 36
        elif c == '-':
            return 62
        elif c == '_':
            return 63
        else:
            raise ValueError("Invalid character in base 64 representation")

    def int_to_base_64(n: int) -> str:
        if 0 <= n <= 9:
            return chr(n + ord('0'))
        elif 10 <= n <= 35:
            return chr(n + ord('A') - 10)
        elif 36 <= n <= 61:
            return chr(n + ord('a') - 36)
        elif n == 62:
            return '-'
        elif n == 63:
            return '_'
        else:
            raise ValueError("Invalid integer value")

    def bitwise_AND(word1: str, word2: str) -> str:
        result = ''
        for i in range(len(word1)):
            result += int_to_base_64(base_64_to_int(word1[i]) & base_64_to_int(word2[i]))
        return result

    def count_pairs(stdin: str, target: str) -> int:
        modulo = 10**9 + 7
        count = 0
        for i in range(len(stdin)):
            for j in range(i+1, len(stdin)+1):
                word1 = stdin[i:j]
                word2 = bitwise_AND(word1, target)
                if len(word1) == len(word2) and word1 == word2:
                    count += 1
        return count % modulo

    stdin = stdin.strip()
    target = ''.join(int_to_base_64(base_64_to_int(c)) for c in stdin)
    result = count_pairs(stdin, target)
    return str(result)

