    m = 3  # maximum number of pennants for a single person
    M = 5  # maximum number of total pennants

    # Calculate the number of ways to place pennants for each person
    ways = [1] * M  # ways[i] stores the number of ways to place i pennants
    for i in range(M):
        for j in range(i):
            ways[i] += ways[j]

    # Calculate the number of ways to place pennants for all people
    total_ways = 0
    for i in range(1, m + 1):
        total_ways += ways[i] * ways[M - i]

    # Divide by n! to get the number of ways to place pennants on n tables
    factorial = 1
    for i in range(1, n + 1):
        factorial *= i
    result = total_ways // factorial

    return str(result)


