    # create a graph from the input pairs
    graph = {i: [] for i in range(1, n + 1)}
    for a, b in pairs:
        graph[a].append(b)
        graph[b].append(a)

    # find the starting node (1)
    for i in range(1, n + 1):
        if 1 in graph[i]:
            starting_node = i
            break

    # find the order of nodes using DFS
    visited = [False] * (n + 1)
    order = []

    def dfs(node: int):
        visited[node] = True
        for neighbor in graph[node]:
            if not visited[neighbor]:
                dfs(neighbor)
        order.append(node)

    dfs(starting_node)

    # check if the order is valid
    if len(set(order)) != n:
        return "-1"

    # print the order of nodes
    return " ".join(map(str, order))

