    n, m = map(int, stdin.split()[0:2])
    roads = [tuple(map(int, line.split())) for line in stdin.split("\n")[1:]]

    def bfs(start, end, graph, visited, is_working):
        queue = [(start, [start])]
        while queue:
            (vertex, path) = queue.pop(0)
            for neighbor, is_neighbor_working in graph[vertex]:
                if neighbor not in visited and (not is_neighbor_working or is_neighbor_working == is_working):
                    new_path = path + [neighbor]
                    queue.append((neighbor, new_path))
                    if neighbor == end:
                        return new_path
        return None

    def find_shortest_path(start, end, graph):
        visited = set()
        return bfs(start, end, graph, visited, True)

    def find_optimal_path(start, end, graph, roads_to_repair, roads_to_blowup, roads_affected, roads_to_blowup_or_repair):
        visited = set()
        queue = [(start, [start], False)]
        while queue:
            (vertex, path, is_path_affected) = queue.pop(0)
            for neighbor, is_neighbor_working in graph[vertex]:
                if neighbor not in visited:
                    new_path = path + [neighbor]
                    new_is_path_affected = is_path_affected or roads_to_blowup.get((vertex, neighbor), 0) or roads_to_repair.get((vertex, neighbor), 0)
                    new_roads_to_blowup_or_repair = roads_to_blowup_or_repair.copy()
                    if not is_neighbor_working:
                        new_roads_to_blowup_or_repair[vertex, neighbor] = 1
                    if neighbor == end:
                        roads_affected.append((vertex, neighbor, new_is_path_affected))
                        return True
                    queue.append((neighbor, new_path, new_is_path_affected))
        return False

    graph = [[] for _ in range(n + 1)]
    for u, v, is_working in roads:
        graph[u].append((v, is_working))
        graph[v].append((u, is_working))

    shortest_path = find_shortest_path(1, n, graph)
    if not shortest_path:
        return "Impossible"

    roads_to_repair = {}
    roads_to_blowup = {}
    for u, v, z in roads:
        if z == 0:
            roads_to_blowup[u, v] = 1
            roads_to_blowup[v, u] = 1
        else:
            roads_to_repair[u, v] = 1
            roads_to_repair[v, u] = 1

    roads_affected = []
    roads_to_blowup_or_repair = roads_to_blowup.copy()
    find_optimal_path(1, n, graph, roads_to_repair, roads_to_blowup, roads_affected, roads_to_blowup_or_repair)

    if not roads_affected:
        return "Impossible"

    k = len(roads_affected)
    return str(k) + "\n" + "\n".join(f"{u} {v} {z}" for u, v, z in roads_affected)

