    n, d = map(int, input().split())
    friends = []
    for _ in range(n):
        m, s = map(int, input().split())
        friends.append((m, s))

    # sort friends by amount of money
    friends.sort(key=lambda x: x[0])

    # calculate the total friendship factor of the selected group
    def calculate_total_friendship(group):
        total_friendship = 0
        for i in range(len(group)):
            for j in range(i + 1, len(group)):
                total_friendship += group[i][1] * group[j][1]
        return total_friendship

    # use dynamic programming to find the optimal group
    dp = [[0] * n for _ in range(d + 1)]
    for i in range(n):
        for j in range(d + 1):
            if j < friends[i][0]:
                dp[j][i] = dp[j][i - 1]
            else:
                dp[j][i] = max(dp[j][i - 1], dp[j - friends[i][0]][i - 1] + friends[i][1])

    # find the optimal group
    group = []
    j = d
    for i in range(n - 1, -1, -1):
        if dp[j][i] == dp[j - friends[i][0]][i - 1] + friends[i][1]:
            group.append(i)
            j -= friends[i][0]

    # calculate the total friendship factor of the selected group
    total_friendship = calculate_total_friendship([friends[i] for i in group])

    return str(total_friendship)

