    graph = [[] for _ in range(n + 1)]
    for a, b in pairs:
        graph[a].append(b)
        graph[b].append(a)

    def dfs(node, visited, depth, depth_list):
        visited.add(node)
        depth_list.append(depth)
        for neighbor in graph[node]:
            if neighbor not in visited:
                dfs(neighbor, visited, depth + 1, depth_list)

    def get_depths(root):
        visited = set()
        depth_list = []
        dfs(root, visited, 0, depth_list)
        return depth_list

    def is_musketeer(root):
        depths = get_depths(root)
        for neighbor in graph[root]:
            if neighbor != root and neighbor in depths:
                return False
        return True

    min_sum = float('inf')
    for root in range(1, n + 1):
        if is_musketeer(root):
            musketeers = [root]
            for neighbor in graph[root]:
                if neighbor != root and is_musketeer(neighbor):
                    musketeers.append(neighbor)
                    break
            if len(musketeers) == 3:
                sum_recognition = sum(len(graph[node]) - 1 for node in musketeers)
                min_sum = min(min_sum, sum_recognition)

    return str(min_sum) if min_sum != float('inf') else '-1'

