        return 0
    elif K == 1:
        return 1
    elif K == 2:
        return 1 if N > 3 else 2
    elif K == 3:
        return 1 if N > 4 else 4

    # Calculate the number of sequences with length K and N
    dp = [0] * (K + 1)
    dp[1] = 1
    dp[2] = 1 if N > 3 else 2
    dp[3] = 1 if N > 4 else 4

    for i in range(4, K + 1):
        if i % 2 == 1:
            if i == 3:
                dp[i] = (N - 1) * dp[i - 1] % MOD
            elif i == 5:
                dp[i] = (N - 1) * dp[i - 1] % MOD
            else:
                dp[i] = (N - 1) * dp[i - 1] % MOD
        else:
            dp[i] = (N - 1) * dp[i - 2] % MOD

    return dp[K]

