    MOD_POWER = pow(2, 31, mod)

    def nCr(n, r):
        if n < r or r < 0:
            return 0
        result = 1
        for i in range(r):
            result = (result * (n - i) % mod) * pow(i + 1, MOD_POWER, mod) % mod
        return result

    def catalan_recursive(n):
        if n <= 1:
            return 1
        return (2 * n - 1) * catalan_recursive(n - 1) // n

    def catalan_number(n):
        if n <= 1:
            return 1
        result = 0
        for i in range(n + 1):
            result += catalan_recursive(i)
        return result % mod

    def beautiful_bicolorings(n, k):
        if n == 1:
            return 2
        if k == 1:
            return 2
        if k == 2:
            return 1
        if k >= n:
            return 0

        B = (n - 1) * (n - 2) // 2
        A = n * (n - 1) * (n - 2) * (n - 3) // 24
        C = catalan_number(n - 1)

