    def is_convex(polygon: list) -> bool:
        n = len(polygon)
        sign = 0
        for i in range(n):
            j = (i + 1) % n
            k = (j + 1) % n
            dx1, dy1 = polygon[j][0] - polygon[i][0], polygon[j][1] - polygon[i][1]
            dx2, dy2 = polygon[k][0] - polygon[j][0], polygon[k][1] - polygon[j][1]
            cross = dx1 * dy2 - dy1 * dx2
            if sign == 0:
                sign = cross
            elif sign * cross < 0:
                return False
        return True

    def min_distance_to_segment(point: tuple, segment: tuple) -> float:
        x1, y1 = segment[0]
        x2, y2 = segment[1]
        x3, y3 = point
        dx, dy = x2 - x1, y2 - y1
        det = dx * dx + dy * dy
        if det == 0:
            return abs(x1 - x3) + abs(y1 - y3)
        a = (x1 * (y2 - y1) + y1 * (x1 - x2)) / det
        if a < 0:
            a = 0
        elif a > 1:
            a = 1
        x, y = x1 + a * dx, y1 + a * dy
        return abs(x - x3) + abs(y - y3)

    n = int(stdin.splitlines()[0])
    polygon = [(int(x), int(y)) for i, line in enumerate(stdin.splitlines()[1:]) for x, y in [tuple(map(int, line.split()))]]

    max_distance = 0
    for i in range(n):
        distances = [min_distance_to_segment(polygon[i], (polygon[j], polygon[(j + 1) % n])) for j in range(n)]
        max_distance = max(max_distance, max(distances))

    if is_convex(polygon):
        return max_distance

    for i in range(n):
        distances = [min_distance_to_segment(polygon[i], (polygon[j], polygon[(j + 1) % n])) for j in range(n)]
        max_distance = max(max_distance, max(distances))
        if not is_convex(polygon):
            break

    return max_distance

