    def suitability(s, t):
        count = 0
        for i in range(len(s)):
            if s[i] == '?':
                continue
            for j in range(len(t)):
                if s[i] == t[j]:
                    count += 1
                    break
        return count

    def replace_question_marks(s):
        result = []
        for c in s:
            if c == '?':
                result.append(chr(ord('a') + len(result) % 26))
            else:
                result.append(c)
        return ''.join(result)

    best_s = replace_question_marks(s)
    best_suitability = suitability(best_s, t)

    for i in range(len(s)):
        if s[i] == '?':
            s[i] = 'a'
            s_with_a = replace_question_marks(s)
            suitability_with_a = suitability(s_with_a, t)
            if suitability_with_a > best_suitability:
                best_s = s_with_a
                best_suitability = suitability_with_a
            s[i] = 'b'
            s_with_b = replace_question_marks(s)
            suitability_with_b = suitability(s_with_b, t)
            if suitability_with_b > best_suitability:
                best_s = s_with_b
                best_suitability = suitability_with_b
            swap(i, i)
