def solution(stdin: str) -> str:
    n, *m = map(int, stdin.split())
    # create a list of tasks assigned to each server
    tasks = [0] * n
    for i in range(n):
        tasks[i] = m[i * n:(i + 1) * n]

    # calculate the maximum tasks per server
    max_tasks = max(sum(t) for t in tasks)

    # calculate the minimum number of seconds needed to balance the load
    seconds = 0
    while max(sum(t) for t in tasks) != max_tasks:
        # find the most loaded server and the least loaded server
        max_server, min_server = 0, 0
        for i in range(n):
            if sum(tasks[i]) > sum(tasks[max_server]):
                max_server = i
            if sum(tasks[i]) < sum(tasks[min_server]):
                min_server = i

        # move a task from the most loaded server to the least loaded server
        for i in range(n):
            if tasks[max_server][i] > tasks[min_server][i]:
                tasks[max_server][i] -= 1
                tasks[min_server][i] += 1
                break

        # increment the number of seconds
        seconds += 1

    return str(seconds)

