    x1, y1, x2, y2 = map(int, stdin.splitlines()[-1].split())

    def distance(x1, y1, x2, y2):
        return abs(x1 - x2) + abs(y1 - y2)

    def is_empty(x, y):
        return 0 <= x < n and 0 <= y < m and room[x][y] == '.'

    def can_move(x, y, dx, dy):
        return is_empty(x + dx, y + dy)

    if not is_empty(x1, y1) or not is_empty(x2, y2):
        return -1

    if distance(x1, y1, x2, y2) == 0:
        return 0

    # Dijkstra's algorithm
    queue = [(x1, y1, 0)]
    visited = set([(x1, y1)])
    dist = {(x1, y1): 0}

    while queue:
        (x, y, d) = queue.pop(0)

        if (x, y) == (x2, y2):
            return d

        for dx, dy in [(-1, 0), (1, 0), (0, -1), (0, 1)]:
            if can_move(x, y, dx, dy) and (x + dx, y + dy) not in visited:
                dist[(x + dx, y + dy)] = d + 1
                visited.add((x + dx, y + dy))
                queue.append((x + dx, y + dy, d + 1))

    return -1


